import { createStore } from 'vuex';
const store = new createStore({
    state() {
        return {
            menu: null,
            item: null
        };
    },
    mutations: {
        setMenu(state, menu) {
            state.menu = menu;
        },
        setItem(state, item) {
            state.item = item;
        }
    },
    actions: {
        updateMenu({ commit }, menu) {
            commit('setMenu', menu);
        },
        loadItem({ commit }, item) {
            commit('setItem', item);
        }
    }
});

export default store;
